mapper.select:
SELECT DISTINCT s.ID id, s.R_CODE resourceCode, s.R_URLPATTERN urlPattern, s.R_TYPE resourceType, s.R_DESC name, s.R_PID parentId, s.R_ORDER, r.ROLE_NAME role FROM MES_RESOURCE s,ROLE_RESOURCE rs ,ROLES r WHERE s.ID=rs.RESOURCEID AND s.R_ISSHOW = '1' AND r.ROLE_ID=rs.ROLEID
#{item}
ORDER BY s.R_ORDER ASC

SELECT DISTINCT s.ID id, s.R_CODE resourceCode, s.R_URLPATTERN urlPattern, s.R_TYPE resourceType, s.R_DESC name, s.R_PID parentId, s.R_ORDER FROM MES_RESOURCE s,ROLE_RESOURCE rs ,ROLES r WHERE s.ID=rs.RESOURCEID AND s.R_ISSHOW = '1' AND r.ROLE_ID=rs.ROLEID
#{item}
ORDER BY s.R_PID, s.R_ORDER

mapper.update:
update MES_USERS
set USERS_FLAG='1'
where USERS_NAME=#{userAccount}
